import React, {Component} from "react";
import "./singlePost.css";
class Post extends Component{
    state={
        username: '',
        title: '',
        content: ''
    }

    setUsername=(e)=>{
        e.preventDefault();
    }
    setTitle=(e)=>{
        e.preventDefault();
    }
    setContent=(e)=>{
        e.preventDefault();
    }
    render(){
        return(
            <section className="post-section">
                <form>
                <h2>Create a Post!</h2>
                    <div className="form-container">
                    <div className="form-input">   
                        <label htmlFor="username">Username:</label>
                        <input value ={this.state.username} onChange={this.setUsername} type="text" id="username"/>
                    </div>
                    <div className="form-input"> 
                        <label htmlFor="title">Title:</label>
                        <input onChange={this.setTitle} type="text" id="title"/>
                    </div>
                    <div className="form-input"> 
                        <label htmlFor="content">Content:</label>
                        <input onChange={this.setContent} type="text" id="content"/>
                    </div>
                    <button className ="create-btn">Create a post</button>
                    </div>
                </form>
            </section>
        )
    }
}
export default Post
